import React from 'react';
import CardList from './CardList';
import {robots} from './robots';


class App extends React.Component {

	constructor() {
		super()
		this.state={
			arrayToRender: robots,
			searchField: ''
		}
	}

	onSearchChange = (_evnt) => {
		const filterResults = this.state.arrayToRender.filter((_robot)=>{return (_robot.name.toLowerCase().includes(_evnt.target.value.toLowerCase()))});
		console.log(filterResults);
	}

	render() {
		return (
		<div className='tc'>
			<div className='pa2'>
				<h1>Robot Friends</h1>
				<input className='pa3 ba b--green bg-lightest-blue' type='search' placeholder='Type something!' onChange={this.onSearchChange}/>
			</div>
			<CardList param1={this.state.arrayToRender} />
			// <CardList param1={this.state.arrayToRender} />
		</div>
		);
	}

}




